import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // ---------------- ASK AI (TEXT) ----------------
  const askAI = async () => {
    // 1Ô∏è‚É£ Send text if any
    if (question.trim()) {
      setMessages((p) => [...p, { text: question, sender: "user" }]);
      try {
        const res = await fetch(
          `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
        );
        const data = await res.json();
        setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
      } catch {
        setMessages((p) => [
          ...p,
          { text: "Error connecting to AI backend", sender: "ai" },
        ]);
      }
    }

    // 2Ô∏è‚É£ Send files if any
    if (selectedFiles.length > 0) {
      const formData = new FormData();
      selectedFiles.forEach((file) => formData.append("files", file));

      try {
        const res = await fetch("http://127.0.0.1:8000/upload", {
          method: "POST",
          body: formData,
        });
        const data = await res.json();
        setMessages((p) => [
          ...p,
          { text: `üìé Uploaded: ${data.uploaded_files.join(", ")}`, sender: "ai" },
        ]);
      } catch {
        setMessages((p) => [
          ...p,
          { text: "‚ùå File upload failed", sender: "ai" },
        ]);
      }

      setSelectedFiles([]); // clear files after sending
    }

    setQuestion(""); // clear text input
  };

  // ---------------- FILE SELECT ----------------
  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 0) setSelectedFiles(files);
    e.target.value = ""; // reset input so same file can be selected again
  };

  const renderAIContent = (text) => {
    let html = text
      .replace(/^### (.*)$/gm, "<h3>üîπ $1</h3>")
      .replace(/^## (.*)$/gm, "<h2>üìå $1</h2>")
      .replace(/^# (.*)$/gm, "<h1>‚≠ê $1</h1>")
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/^\d+\.\s+(.*)$/gm, "<li>‚û°Ô∏è $1</li>")
      .replace(/^\-\s+(.*)$/gm, "<li>üëâ $1</li>")
      .replace(/\n/g, "<br/>");

    html = html.replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");
    return { __html: html };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
        }}
      >
        <h2>AI Assistant</h2>
      </div>

      {/* CHAT */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: "#FFF7E6",
            borderRadius: "10px",
            border: "1px solid #ccc",
          }}
        >
          {messages.map((m, i) => (
            <div key={i} style={{ marginBottom: "12px" }}>
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              {m.sender === "ai" ? (
                <div
                  style={{ marginTop: "6px" }}
                  dangerouslySetInnerHTML={renderAIContent(m.text)}
                />
              ) : (
                <div style={{ marginTop: "6px" }}>{m.text}</div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div style={{ display: "flex", padding: "10px" }}>
          {/* + BUTTON */}
          <label
            htmlFor="fileInput"
            style={{
              width: "36px",
              height: "36px",
              borderRadius: "50%",
              background: "#007bff",
              color: "#fff",
              fontSize: "22px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
              marginRight: "8px",
            }}
          >
            +
          </label>

          {/* FILE + CAMERA INPUT */}
          <input
            id="fileInput"
            type="file"
            multiple
            accept="image/*,application/pdf"
            capture="environment"
            style={{ display: "none" }}
            onChange={handleFileUpload}
          />

          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{ flex: 1, padding: "10px" }}
          />

          <button onClick={askAI} style={{ marginLeft: "8px" }}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
