import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const renderAIContent = (html) => {
    const container = document.createElement("div");
    container.innerHTML = html;

    container.querySelectorAll("pre").forEach((pre) => {
      pre.style.position = "relative";
      pre.style.paddingTop = "34px";

      const btn = document.createElement("button");
      btn.innerText = "Copy";
      btn.style.position = "absolute";
      btn.style.top = "6px";
      btn.style.right = "6px";
      btn.style.fontSize = "12px";

      btn.onclick = () => {
        navigator.clipboard.writeText(pre.innerText);
        btn.innerText = "Copied âœ“";
        setTimeout(() => (btn.innerText = "Copy"), 1500);
      };

      pre.appendChild(btn);
    });

    return { __html: container.innerHTML };
  };

  return (
    <div
      className="chat-wrapper"
      style={{
        background: darkMode ? "#121212" : "#ffffff",
        color: darkMode ? "#fff" : "#000",
      }}
    >
      {/* INPUT BAR (TOP â€“ ChatGPT style) */}
      <div className="input-top">
        <input
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
          placeholder="Ask anything..."
        />
        <button onClick={askAI}>âž¤</button>

        <button
          className="mode-btn"
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? "â˜€" : "ðŸŒ™"}
        </button>
      </div>

      {/* CHAT AREA */}
      <div className="chat-box">
        {messages.map((m, i) => (
          <div key={i} className={`msg ${m.sender}`}>
            <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>

            {m.sender === "ai" ? (
              <div
                dangerouslySetInnerHTML={renderAIContent(m.text)}
              />
            ) : (
              <div>{m.text}</div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
    </div>
  );
}

export default Chat;
