import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [files, setFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState({});
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, files]);

  // ---------------- AI Chat ----------------
  const askAI = async (text) => {
    if (!text.trim()) return;

    setMessages((p) => [...p, { text, sender: "user" }]);

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(text)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  // ---------------- File Upload ----------------
  const uploadFile = (file) => {
    const formData = new FormData();
    formData.append("files", file);

    try {
      const xhr = new XMLHttpRequest();
      xhr.open("POST", "http://127.0.0.1:8000/upload");

      xhr.upload.onprogress = (event) => {
        if (event.lengthComputable) {
          setUploadProgress((prev) => ({
            ...prev,
            [file.name]: Math.round((event.loaded / event.total) * 100),
          }));
        }
      };

      xhr.onload = () => {
        if (xhr.status === 200) {
          const data = JSON.parse(xhr.responseText);
          setMessages((p) => [
            ...p,
            { text: `Uploaded: ${data.uploaded_files.join(", ")}`, sender: "ai" },
          ]);
          setUploadProgress((prev) => {
            const updated = { ...prev };
            delete updated[file.name];
            return updated;
          });
        } else {
          setMessages((p) => [
            ...p,
            { text: `Failed to upload ${file.name}`, sender: "ai" },
          ]);
        }
      };

      xhr.send(formData);
    } catch (err) {
      console.error(err);
      setMessages((p) => [
        ...p,
        { text: `Error uploading ${file.name}`, sender: "ai" },
      ]);
    }
  };

  const handleFiles = (selectedFiles) => {
    const newFiles = Array.from(selectedFiles);
    setFiles((prev) => [...prev, ...newFiles]);
    newFiles.forEach(uploadFile);
  };

  const handleFileChange = (e) => handleFiles(e.target.files);
  const handleDrop = (e) => { e.preventDefault(); handleFiles(e.dataTransfer.files); };
  const handleDragOver = (e) => e.preventDefault();

  // ---------------- Camera ----------------
  const startCamera = async () => {
    setCameraActive(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    } catch (err) {
      console.error("Camera access denied", err);
      setCameraActive(false);
    }
  };

  const capturePhoto = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    canvas.toBlob((blob) => {
      const file = new File([blob], `photo_${Date.now()}.png`, { type: "image/png" });
      handleFiles([file]);
    });
    stopCamera();
  };

  const stopCamera = () => {
    const stream = videoRef.current.srcObject;
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    setCameraActive(false);
  };

  // ---------------- AI Content Formatter ----------------
  const renderAIContent = (text) => {
    let html = text
      .replace(/^### (.*)$/gm, "<h3>üîπ $1</h3>")
      .replace(/^## (.*)$/gm, "<h2>üìå $1</h2>")
      .replace(/^# (.*)$/gm, "<h1>‚≠ê $1</h1>")
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/^\d+\.\s+(.*)$/gm, "<li>‚û°Ô∏è $1</li>")
      .replace(/^\-\s+(.*)$/gm, "<li>üëâ $1</li>")
      .replace(/\n/g, "<br/>");

    html = html.replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");
    return { __html: html };
  };

  // ---------------- SEND BUTTON ----------------
  const handleSend = async () => {
    if (question.trim()) await askAI(question);
    if (files.length > 0) {
      files.forEach(uploadFile);
      setFiles([]); // clear after sending
    }
    setQuestion("");
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>AI Assistant</h2>
        <button style={{ padding: "10px", borderRadius: "6px", border: "none", cursor: "pointer", marginBottom: "12px" }}>+ New Chat</button>
        <div style={{ opacity: 0.7 }}>History</div>
      </div>

      {/* CHAT SECTION */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div style={{ flex: 1, margin: "10px", padding: "14px", overflowY: "auto", background: darkMode ? "#1e1e1e" : "#FFF7E6", borderRadius: "10px", border: "1px solid #ccc", position: "relative" }}>
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{ position: "fixed", top: "16px", right: "16px", borderRadius: "20px", padding: "6px 14px", border: "none", cursor: "pointer", background: darkMode ? "#FFF7E6" : "#222", color: darkMode ? "#000" : "#fff", zIndex: 1000 }}
          >
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>

          {messages.map((m, i) => (
            <div key={i} style={{ marginBottom: "14px", padding: "14px", borderRadius: "8px", border: "1px solid #ddd", background: m.sender === "user" ? (darkMode ? "#FFF7E6" : "#000") : (darkMode ? "#2a2a2a" : "#fff"), color: m.sender === "user" ? (darkMode ? "#000" : "#fff") : (darkMode ? "#fff" : "#000") }}>
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              <div style={{ marginTop: "6px" }} dangerouslySetInnerHTML={m.sender === "ai" ? renderAIContent(m.text) : undefined}>
                {m.sender !== "ai" && m.text}
              </div>
            </div>
          ))}

          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div onDrop={handleDrop} onDragOver={handleDragOver} style={{ display: "flex", flexDirection: "column", padding: "10px", borderTop: "1px solid #ccc", background: darkMode ? "#121212" : "#ffffff" }}>
          <div style={{ display: "flex", alignItems: "center" }}>
            {/* Camera Button */}
            <button onClick={startCamera} style={{ cursor: "pointer", background: "#28a745", color: "#fff", fontWeight: "bold", fontSize: "18px", width: "36px", height: "36px", borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center", marginRight: "6px" }} title="Camera">üì∑</button>

            {/* File Upload Button */}
            <label htmlFor="fileUpload" style={{ cursor: "pointer", background: "#007bff", color: "#fff", fontWeight: "bold", fontSize: "20px", width: "36px", height: "36px", borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center", marginRight: "8px" }} title="Add Files">+</label>
            <input id="fileUpload" type="file" multiple style={{ display: "none" }} onChange={handleFileChange} />

            {/* Text Input */}
            <input value={question} onChange={(e) => setQuestion(e.target.value)} onKeyDown={(e) => e.key === "Enter" && handleSend()} placeholder="Message AI..." style={{ flex: 1, padding: "10px 16px", borderRadius: "20px", border: "1px solid #ccc", marginRight: "8px" }} />

            {/* Send Button */}
            <button onClick={handleSend} style={{ padding: "10px 18px", background: "#007bff", color: "#fff", border: "none", borderRadius: "20px" }}>Send</button>
          </div>

          {/* Camera Preview */}
          {cameraActive && (
            <div style={{ marginTop: "6px", position: "relative" }}>
              <video ref={videoRef} style={{ width: "200px", borderRadius: "8px" }} />
              <button onClick={capturePhoto} style={{ position: "absolute", bottom: "4px", left: "4px", padding: "4px 8px", borderRadius: "6px", background: "#28a745", color: "#fff", border: "none", cursor: "pointer" }}>Capture</button>
              <button onClick={stopCamera} style={{ position: "absolute", bottom: "4px", right: "4px", padding: "4px 8px", borderRadius: "6px", background: "#dc3545", color: "#fff", border: "none", cursor: "pointer" }}>Close</button>
              <canvas ref={canvasRef} style={{ display: "none" }} />
            </div>
          )}

          {/* File Previews + Progress */}
          {files.length > 0 && (
            <div style={{ display: "flex", flexDirection: "column", marginTop: "6px" }}>
              {files.map((file, i) => (
                <div key={i} style={{ marginBottom: "6px" }}>
                  {file.type.startsWith("image/") && <img src={URL.createObjectURL(file)} alt={file.name} style={{ width: "100px", borderRadius: "8px", marginBottom: "2px" }} />}
                  {file.type === "application/pdf" && <div style={{ width: "100px", height: "140px", border: "1px solid #ccc", borderRadius: "6px", display: "flex", alignItems: "center", justifyContent: "center", fontSize: "12px", marginBottom: "2px" }}>PDF: {file.name}</div>}
                  <div style={{ display: "flex", alignItems: "center" }}>
                    <span style={{ background: "#2a2a2a", color: "#fff", padding: "4px 8px", borderRadius: "12px", fontSize: "12px", marginRight: "6px", display: "inline-block" }}>{file.name}</span>
                    {uploadProgress[file.name] && <div style={{ height: "6px", width: `${uploadProgress[file.name]}%`, background: "#007bff", borderRadius: "3px", transition: "width 0.2s" }} />}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Chat;
