import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    const res = await fetch(
      `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
    );
    const data = await res.json();

    setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
  };

  const renderAIContent = (html) => {
    const div = document.createElement("div");
    div.innerHTML = html;

    div.querySelectorAll("pre").forEach((pre) => {
      pre.style.position = "relative";
      pre.style.paddingTop = "34px";

      const btn = document.createElement("button");
      btn.innerText = "Copy";
      btn.style.position = "absolute";
      btn.style.top = "6px";
      btn.style.right = "6px";
      btn.style.fontSize = "12px";
      btn.style.border = "none";
      btn.style.borderRadius = "6px";
      btn.style.padding = "4px 8px";
      btn.style.cursor = "pointer";

      btn.onclick = () => {
        navigator.clipboard.writeText(pre.innerText);
        btn.innerText = "Copied âœ“";
        setTimeout(() => (btn.innerText = "Copy"), 1500);
      };

      pre.appendChild(btn);
    });

    return { __html: div.innerHTML };
  };

  return (
    <div
      style={{
        height: "100%",
        display: "flex",
        flexDirection: "column",
        padding: "16px",
        background: darkMode ? "#121212" : "#ffffff",
      }}
    >
      {/* HEADER */}
      <div style={{ marginBottom: "10px" }}>
        <button onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
        </button>
      </div>

      {/* MESSAGES */}
      <div
        style={{
          flex: 1,
          overflowY: "auto",
        }}
      >
        {messages.map((m, i) => (
          <div
            key={i}
            style={{
              maxWidth: "800px",
              marginBottom: "16px",
              padding: "14px",
              borderRadius: "8px",
              background:
                m.sender === "user"
                  ? darkMode
                    ? "#FFF7E6"
                    : "#000"
                  : darkMode
                  ? "#2a2a2a"
                  : "#fff",
              color:
                m.sender === "user"
                  ? darkMode
                    ? "#000"
                    : "#fff"
                  : darkMode
                  ? "#fff"
                  : "#000",
              textAlign: "left",
            }}
          >
            <strong>{m.sender === "ai" ? "AI" : "You"}</strong>
            <div
              style={{ marginTop: "6px" }}
              dangerouslySetInnerHTML={
                m.sender === "ai"
                  ? renderAIContent(m.text)
                  : undefined
              }
            >
              {m.sender === "user" && m.text}
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* INPUT */}
      <div style={{ display: "flex", marginTop: "10px" }}>
        <input
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
          placeholder="Message AI..."
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
          }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "8px",
            padding: "10px 16px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
