import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}
      );
      const data = await response.json();

      let aiText =
        typeof data.answer === "string"
          ? data.answer
          : JSON.stringify(data.answer);

      const formattedText = aiText
        .replace(/```/g, "<pre>")
        .replace(/\n/g, "<br/>")
        .replace(/^\* /gm, "• ");

      setMessages((prev) => [...prev, { text: formattedText, sender: "ai" }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "900px",
        margin: "20px auto",
        fontFamily: "Arial",
      }}
    >
      <div
        style={{
          background: "#FFF7E6", // cream background
          border: "1px solid #ccc",
          height: "450px",
          overflowY: "auto",
          padding: "20px",
          borderRadius: "10px",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              marginBottom: "20px",
              padding: "15px",
              width: "100%",
              textAlign: "left", // LEFT ALIGN ALWAYS
              background: msg.sender === "user" ? "#000000" : "#FFFFFF",
              color: msg.sender === "user" ? "#FFFFFF" : "#000000",
              borderRadius: "8px",
              fontSize: "16px",
              lineHeight: "1.5",
              border: "1px solid #ddd",
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>
            <br />
            {msg.sender === "ai" ? (
              <div
                dangerouslySetInnerHTML={{ __html: msg.text }}
                style={{ marginTop: "8px" }}
              />
            ) : (
              <div style={{ marginTop: "8px" }}>{msg.text}</div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div style={{ marginTop: "12px", display: "flex" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            fontSize: "16px",
          }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "8px",
            padding: "10px 20px",
            borderRadius: "6px",
            border: "none",
            background: "#007bff",
            color: "white",
            fontSize: "16px",
            cursor: "pointer",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
