import React, { useState } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]); // store all messages

  const askAI = async () => {
    if (!question) return;

    // Add user's message
    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await response.json();

      // Add AI's response
      setMessages((prev) => [...prev, { text: data.answer || "No response from AI", sender: "ai" }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [...prev, { text: "Error connecting to AI backend", sender: "ai" }]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages" style={{ border: "1px solid gray", padding: "10px", height: "300px", overflowY: "scroll" }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.sender === "ai" ? "left" : "right", margin: "5px 0" }}>
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong> {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Ask me something..."
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        style={{ width: "80%", marginTop: "10px" }}
      />
      <button onClick={askAI} style={{ width: "18%", marginLeft: "2%" }}>Ask AI</button>
    </div>
  );
}

export default Chat;
