import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;
    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");
    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const renderAIContent = (html) => {
    const container = document.createElement("div");

   // Replace **text** with <strong>text</strong>
  html = html.replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>");

  // Replace numbered headings like 1. Title with <h2>Title</h2>
  html = html.replace(/(\d+\.\s)(.*)/g, "<h2>$2</h2>");

    container.innerHTML = html;

    container.querySelectorAll("pre").forEach((pre) => {
      pre.style.position = "relative";
      pre.style.paddingTop = "32px";

      const btn = document.createElement("button");
      btn.innerText = "Copy";
      btn.style.position = "absolute";
      btn.style.top = "6px";
      btn.style.right = "6px";
      btn.style.border = "none";
      btn.style.borderRadius = "6px";
      btn.style.padding = "4px 8px";
      btn.style.cursor = "pointer";
      btn.style.fontSize = "12px";

      btn.onclick = () => {
        navigator.clipboard.writeText(pre.innerText);
        btn.innerText = "Copied âœ“";
        setTimeout(() => (btn.innerText = "Copy"), 1500);
      };

      pre.appendChild(btn);
    });

    return { __html: container.innerHTML };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>AI Assistant</h2>
        <button
          style={{
            padding: "10px",
            borderRadius: "6px",
            border: "none",
            cursor: "pointer",
            marginBottom: "12px",
          }}
        >
          + New Chat
        </button>
        <div style={{ opacity: 0.7 }}>History</div>
      </div>

      {/* CHAT SECTION */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        {/* CHAT BOX */}
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
            borderRadius: "10px",
            border: "1px solid #ccc",
            position: "relative",
          }}
        >
          {/* THEME TOGGLE */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{
              position: "fixed",
              top: "16px",
              right: "16px",
              borderRadius: "20px",
              padding: "6px 14px",
              border: "none",
              cursor: "pointer",
              background: darkMode ? "#FFF7E6" : "#222",
              color: darkMode ? "#000" : "#fff",
              zIndex: 1000, // scroll ke upar dikhane ke liye
            }}
          >
            {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
          </button>

          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "14px",
                padding: "14px",
                borderRadius: "8px",
                border: "1px solid #ddd",
                background:
                  m.sender === "user"
                    ? darkMode
                      ? "#FFF7E6"
                      : "#000"
                    : darkMode
                    ? "#2a2a2a"
                    : "#fff",
                color:
                  m.sender === "user"
                    ? darkMode
                      ? "#000"
                      : "#fff"
                    : darkMode
                    ? "#fff"
                    : "#000",
              }}
            >
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              {m.sender === "ai" ? (
                <div
                  style={{ marginTop: "6px", overflowX: "auto" }}
                  dangerouslySetInnerHTML={renderAIContent(m.text)}
                />
              ) : (
                <div style={{ marginTop: "6px" }}>{m.text}</div>
              )}
            </div>
          ))}

          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div
          style={{
            display: "flex",
            padding: "10px",
            borderTop: "1px solid #ccc",
            background: darkMode ? "#121212" : "#ffffff",
          }}
        >
          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{
              flex: 1,
              padding: "10px",
              borderRadius: "6px",
              border: "1px solid #ccc",
            }}
          />
          <button
            onClick={askAI}
            style={{
              marginLeft: "8px",
              padding: "10px 18px",
              background: "#007bff",
              color: "#fff",
              border: "none",
              borderRadius: "6px",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
