import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recorder, setRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // ---------- Send text question ----------
  const sendQuestion = async (text) => {
    if (!text || !text.trim()) return;
    // push user text
    setMessages((prev) => [...prev, { text: text.trim(), sender: "user" }]);

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(text)}`
      );
      const data = await response.json();
      const aiText = typeof data.answer === "string" ? data.answer : JSON.stringify(data.answer);
      const formatted = aiText.replace(/```/g, "<pre>").replace(/\n/g, "<br/>").replace(/^\* /gm, "‚Ä¢ ");
      setMessages((prev) => [...prev, { text: formatted, sender: "ai" }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [...prev, { text: "Error connecting to AI backend", sender: "ai" }]);
    }
  };

  // ---------- Click Ask ----------
  const askAI = async () => {
    if (!question.trim()) return;
    const txt = question;
    setQuestion("");
    await sendQuestion(txt);
  };

  // ---------- KEY press Enter ----------
  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  // ---------- Start recording ----------
  const startRecording = async () => {
    if (isRecording) return;
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mr = new MediaRecorder(stream);
      setRecorder(mr);
      setAudioChunks([]);

      mr.ondataavailable = (ev) => {
        if (ev.data && ev.data.size > 0) {
          setAudioChunks((prev) => [...prev, ev.data]);
        }
      };

      mr.onstop = async () => {
        // create a single blob
        const blob = new Blob(audioChunks, { type: "audio/webm" });
        await uploadAudio(blob);
        // stop all tracks to free mic
        stream.getTracks().forEach((t) => t.stop());
        setRecorder(null);
        setAudioChunks([]);
        setIsRecording(false);
      };

      mr.start();
      setIsRecording(true);
    } catch (err) {
      console.error("Microphone access denied or not available", err);
      alert("Cannot access microphone. Please allow mic permissions or use a supported browser.");
    }
  };

  // ---------- Stop recording ----------
  const stopRecording = () => {
    if (!recorder) return;
    recorder.stop();
  };

  // ---------- Upload audio to backend ----------
  // Expects backend at POST /voice to accept form field "file" (audio) and return JSON { answer: "..." }
  const uploadAudio = async (blob) => {
    // Show a placeholder user message that voice was sent
    setMessages((prev) => [...prev, { text: "[Voice message sent ‚Äî processing...]", sender: "user" }]);

    try {
      const fd = new FormData();
      // name the file with .webm extension ‚Äî backend should accept common audio types
      fd.append("file", blob, "voice.webm");

      const resp = await fetch("http://127.0.0.1:8000/voice", {
        method: "POST",
        body: fd,
      });
      const data = await resp.json();
      const aiText = typeof data.answer === "string" ? data.answer : JSON.stringify(data.answer);
      const formatted = aiText.replace(/```/g, "<pre>").replace(/\n/g, "<br/>").replace(/^\* /gm, "‚Ä¢ ");

      // remove the last placeholder user message and add final messages
      setMessages((prev) => {
        const withoutPlaceholder = prev.filter((m) => m.text !== "[Voice message sent ‚Äî processing...]" || m.sender !== "user");
        return [...withoutPlaceholder, { text: formatted, sender: "ai" }];
      });
    } catch (err) {
      console.error("Voice upload error:", err);
      setMessages((prev) => [...prev, { text: "Error uploading voice", sender: "ai" }]);
    }
  };

  return (
    <div style={{ width: "100%", maxWidth: "900px", margin: "20px auto", fontFamily: "Arial" }}>
      {/* Chat area (cream background) */}
      <div style={{
        background: "#FFF7E6",
        border: "1px solid #ccc",
        height: "450px",
        overflowY: "auto",
        padding: "20px",
        borderRadius: "10px"
      }}>
        {messages.map((msg, index) => (
          <div key={index} style={{
            marginBottom: "20px",
            padding: "15px",
            width: "100%",
            textAlign: "left",
            background: msg.sender === "user" ? "#000000" : "#FFFFFF",
            color: msg.sender === "user" ? "#FFFFFF" : "#000000",
            borderRadius: "8px",
            fontSize: "16px",
            lineHeight: "1.5",
            border: "1px solid #ddd"
          }}>
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>
            <br />
            {msg.sender === "ai" ? (
              <div dangerouslySetInnerHTML={{ __html: msg.text }} style={{ marginTop: "8px" }} />
            ) : (
              <div style={{ marginTop: "8px" }}>{msg.text}</div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Controls: input, Ask, Mic */}
      <div style={{ marginTop: "12px", display: "flex", alignItems: "center", gap: "8px" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            fontSize: "16px"
          }}
          disabled={isRecording}
        />

        <button
          onClick={askAI}
          style={{
            padding: "10px 16px",
            borderRadius: "6px",
            border: "none",
            background: "#007bff",
            color: "white",
            fontSize: "16px",
            cursor: "pointer"
          }}
          disabled={isRecording}
        >
          Ask
        </button>

        {/* Mic button */}
        <button
          onMouseDown={startRecording}
          onMouseUp={stopRecording}
          onTouchStart={startRecording}
          onTouchEnd={stopRecording}
          title={isRecording ? "Recording... release to send" : "Hold to record voice"}
          style={{
            padding: "10px 12px",
            borderRadius: "6px",
            border: "none",
            background: isRecording ? "#d9534f" : "#111111",
            color: "#fff",
            cursor: "pointer",
            fontSize: "16px"
          }}
        >
          {isRecording ? "‚óè Recording" : "üé§ Hold to talk"}
        </button>
      </div>
    </div>
  );
}

export default Chat;
