import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(false); // default LIGHT
  const [files, setFiles] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // ---------------- ASK AI ----------------
  const askAI = async () => {
    if (!question.trim() && files.length === 0) return;

    if (question.trim()) {
      setMessages((p) => [...p, { text: question, sender: "user" }]);
      setQuestion("");
    }

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  // ---------------- FILE UPLOAD ----------------
  const handleFileChange = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(selectedFiles);

    const formData = new FormData();
    selectedFiles.forEach((file) => formData.append("files", file));

    try {
      const res = await fetch("http://127.0.0.1:8000/upload", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setMessages((p) => [
        ...p,
        {
          text: `Uploaded: ${data.uploaded_files.join(", ")}`,
          sender: "ai",
        },
      ]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "File upload failed", sender: "ai" },
      ]);
    }
  };

  // ---------------- FORMAT AI TEXT ----------------
  const renderAIContent = (text) => {
    let html = text
      .replace(/^### (.*)$/gm, "<h3>üîπ $1</h3>")
      .replace(/^## (.*)$/gm, "<h2>üìå $1</h2>")
      .replace(/^# (.*)$/gm, "<h1>‚≠ê $1</h1>")
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/^\d+\.\s+(.*)$/gm, "<li>‚û°Ô∏è $1</li>")
      .replace(/^\-\s+(.*)$/gm, "<li>üëâ $1</li>")
      .replace(/\n/g, "<br/>");

    html = html.replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");
    return { __html: html };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
        }}
      >
        <h2>AI Assistant</h2>
      </div>

      {/* CHAT */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
            borderRadius: "10px",
            border: "1px solid #ccc",
          }}
        >
          {/* THEME */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{
              position: "fixed",
              top: "16px",
              right: "16px",
              padding: "6px 14px",
              borderRadius: "20px",
              border: "none",
              cursor: "pointer",
            }}
          >
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>

          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "12px",
                padding: "12px",
                borderRadius: "8px",
                background: m.sender === "user" ? "#000" : "#fff",
                color: m.sender === "user" ? "#fff" : "#000",
              }}
            >
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              {m.sender === "ai" ? (
                <div dangerouslySetInnerHTML={renderAIContent(m.text)} />
              ) : (
                <div>{m.text}</div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            padding: "10px",
            borderTop: "1px solid #ccc",
            background: "#fff",
          }}
        >
          {/* MERGED + BUTTON */}
          <label
            htmlFor="fileUpload"
            style={{
              width: "38px",
              height: "38px",
              borderRadius: "50%",
              background: "#007bff",
              color: "#fff",
              fontSize: "22px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
              marginRight: "8px",
            }}
            title="Add files or camera"
          >
            +
          </label>
          <input
            id="fileUpload"
            type="file"
            multiple
            accept="image/*,application/pdf"
            capture="environment"
            style={{ display: "none" }}
            onChange={handleFileChange}
          />

          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{
              flex: 1,
              padding: "10px 16px",
              borderRadius: "20px",
              border: "1px solid #ccc",
              marginRight: "8px",
            }}
          />

          <button
            onClick={askAI}
            style={{
              padding: "10px 18px",
              background: "#007bff",
              color: "#fff",
              border: "none",
              borderRadius: "20px",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
