import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");
    setIsTyping(true);

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((prev) => [...prev, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  // ðŸ”¹ Copy ONLY the clicked code block
  const copyCodeFromPre = (preEl) => {
    navigator.clipboard.writeText(preEl.innerText);
    alert("Code copied!");
  };

  // ðŸ”¹ Render AI HTML + inject copy button per <pre>
  const renderAIContent = (html) => {
    const container = document.createElement("div");
    container.innerHTML = html;

    container.querySelectorAll("pre").forEach((pre) => {
      pre.style.position = "relative";
      pre.style.paddingTop = "36px";

      const btn = document.createElement("button");
      btn.innerText = "ðŸ“‹ Copy";
      btn.style.position = "absolute";
      btn.style.top = "6px";
      btn.style.right = "6px";
      btn.style.fontSize = "12px";
      btn.style.cursor = "pointer";
      btn.style.padding = "4px 8px";
      btn.style.borderRadius = "6px";
      btn.style.border = "1px solid #ccc";
      btn.style.background = "#f5f5f5";

      btn.onclick = () => copyCodeFromPre(pre);
      pre.appendChild(btn);
    });

    return { __html: container.innerHTML };
  };

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "900px",
        margin: "20px 0 20px 0",
        alignSelf: "flex-start",

        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#ffffff",
        color: darkMode ? "#ffffff" : "#000000",
        padding: "10px",
        borderRadius: "10px",
      }}
    >
      {/* HEADER */}
      <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
        <h2 style={{ margin: 0 }}>AI Assistant</h2>
        <button onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
        </button>
      </div>

      {/* CHAT BOX */}
      <div
        style={{
          background: darkMode ? "#1e1e1e" : "#FFF7E6",
          border: "1px solid #ccc",
          height: "450px",
          overflowY: "auto",
          overflowX: "hidden",
          padding: "16px",
          borderRadius: "10px",
          marginTop: "10px",
        }}
      >
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              marginBottom: "16px",
              padding: "14px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              background:
                msg.sender === "user"
                  ? darkMode
                    ? "#FFF7E6"
                    : "#000000"
                  : darkMode
                  ? "#2a2a2a"
                  : "#ffffff",
              color:
                msg.sender === "user"
                  ? darkMode
                    ? "#000000"
                    : "#ffffff"
                  : darkMode
                  ? "#ffffff"
                  : "#000000",
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>

            {msg.sender === "ai" ? (
              <div
                style={{ marginTop: "8px", overflowX: "auto" }}
                dangerouslySetInnerHTML={renderAIContent(msg.text)}
              />
            ) : (
              <div style={{ marginTop: "8px" }}>{msg.text}</div>
            )}
          </div>
        ))}

        {isTyping && (
          <div style={{ fontStyle: "italic", opacity: 0.7 }}>
            AI is typing...
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* INPUT */}
      <div style={{ marginTop: "12px", display: "flex" }}>
        <input
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder="Ask me something..."
          style={{ flex: 1, padding: "10px" }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "8px",
            padding: "10px 20px",
            background: "#007bff",
            color: "#fff",
            border: "none",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
