import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    // Add user's message
    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(
          "Answer concisely: " + question
        )}`
      );

      const data = await response.json();

      // --- FIX: ENSURE STRING ALWAYS ---
      let aiText = "";

      if (typeof data.answer === "string") {
        aiText = data.answer;
      } else {
        aiText = JSON.stringify(data.answer); // convert object → string
      }

      setMessages((prev) => [...prev, { text: aiText, sender: "ai" }]);

    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  return (
    <div
      style={{
        maxWidth: "500px",
        margin: "50px auto",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <div
        style={{
          border: "1px solid gray",
          padding: "10px",
          height: "400px",
          overflowY: "scroll",
          borderRadius: "8px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              textAlign: msg.sender === "ai" ? "left" : "right",
              margin: "8px 0",
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong> {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ marginTop: "10px", display: "flex" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: "8px",
            borderRadius: "4px",
            border: "1px solid gray",
          }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "5px",
            padding: "8px 15px",
            borderRadius: "4px",
            border: "none",
            backgroundColor: "#007bff",
            color: "white",
            cursor: "pointer",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
