import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    // Show user question
    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await response.json();

      let aiText =
        typeof data.answer === "string"
          ? data.answer
          : JSON.stringify(data.answer);

      const formattedText = aiText
        .replace(/```/g, "<pre>")
        .replace(/\n/g, "<br/>")
        .replace(/^\* /gm, "â€¢ ");

      setMessages((prev) => [...prev, { text: formattedText, sender: "ai" }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const startVoice = async () => {
    setMessages((prev) => [
      ...prev,
      { text: "[Voice message sent â€” processing...]", sender: "user" },
    ]);

    try {
      const res = await fetch("http://127.0.0.1:8000/start-voice");

      if (!res.ok) {
        setMessages((prev) => [...prev, { text: "Error starting voice", sender: "ai" }]);
        return;
      }

      const data = await res.json();

      setMessages((prev) => [
        ...prev,
        { text: data.answer || "Voice processed successfully", sender: "ai" },
      ]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { text: "Error uploading voice", sender: "ai" },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  return (
    <div
      style={{
        maxWidth: "900px",
        margin: "30px auto",
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#fdf5e6", // cream bg
        padding: "15px",
        borderRadius: "10px",
      }}
    >
      <div
        style={{
          padding: "10px",
          height: "420px",
          overflowY: "auto",
          borderRadius: "8px",
          backgroundColor: "#ffffff",
        }}
      >
        {messages.map((msg, index) => (
          <div key={index} style={{ margin: "8px 0" }}>
            {msg.sender === "ai" ? (
              <div
                style={{
                  textAlign: "left",
                  background: "white",
                  color: "black",
                  padding: "10px",
                  borderRadius: "6px",
                  width: "100%",
                }}
              >
                <strong>AI:</strong>
                <div
                  dangerouslySetInnerHTML={{ __html: msg.text }}
                  style={{ whiteSpace: "pre-wrap", fontFamily: "monospace" }}
                />
              </div>
            ) : (
              <div
                style={{
                  textAlign: "left",
                  background: "black",
                  color: "white",
                  padding: "10px",
                  borderRadius: "6px",
                  width: "100%",
                }}
              >
                <strong>You:</strong> {msg.text}
              </div>
            )}
          </div>
        ))}

        <div ref={messagesEndRef} />
      </div>

      <div style={{ marginTop: "12px", display: "flex", gap: "8px" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid gray",
            fontSize: "15px",
          }}
        />

        {/* MIC BUTTON */}
        <button
          onClick={startVoice}
          style={{
            padding: "8px 12px",
            backgroundColor: "#222",
            color: "white",
            border: "none",
            borderRadius: "50%",
            fontSize: "18px",
            cursor: "pointer",
          }}
        >
          ðŸŽ¤
        </button>

        <button
          onClick={askAI}
          style={{
            padding: "10px 18px",
            borderRadius: "6px",
            border: "none",
            backgroundColor: "#007bff",
            color: "white",
            cursor: "pointer",
            fontSize: "15px",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
