import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        margin: 0,
        padding: 0,
        background: darkMode ? "#121212" : "#ffffff",
        display: "flex",
        justifyContent: "flex-start",
      }}
    >
      {/* MAIN CHAT PANEL */}
      <div
        style={{
          width: "900px",
          marginLeft: "16px",
          marginTop: "16px",
          marginBottom: "16px",
          background: darkMode ? "#1e1e1e" : "#FFF7E6",
          borderRadius: "14px",
          display: "flex",
          flexDirection: "column",
          overflow: "hidden",
          position: "relative",
        }}
      >
        {/* DARK MODE BUTTON */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          style={{
            position: "absolute",
            top: "12px",
            right: "12px",
            background: "#111",
            color: "#fff",
            border: "none",
            borderRadius: "20px",
            padding: "6px 14px",
            cursor: "pointer",
          }}
        >
          {darkMode ? "ðŸŒ™ Dark" : "â˜€ Light"}
        </button>

        {/* CHAT MESSAGES */}
        <div
          style={{
            flex: 1,
            padding: "16px",
            overflowY: "auto",
          }}
        >
          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "14px",
                padding: "12px",
                borderRadius: "8px",
                textAlign: "left",
                background:
                  m.sender === "user"
                    ? darkMode
                      ? "#FFF7E6"
                      : "#000000"
                    : darkMode
                    ? "#2a2a2a"
                    : "#ffffff",
                color:
                  m.sender === "user"
                    ? darkMode
                      ? "#000000"
                      : "#ffffff"
                    : darkMode
                    ? "#ffffff"
                    : "#000000",
              }}
            >
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              <div
                style={{
                  marginTop: "6px",
                  whiteSpace: "pre-wrap", // âœ… FIXED RESPONSE ISSUE
                }}
              >
                {m.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div
          style={{
            display: "flex",
            padding: "12px",
            borderTop: "1px solid #ccc",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
          }}
        >
          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{
              flex: 1,
              padding: "10px",
              borderRadius: "6px",
              border: "1px solid #ccc",
              outline: "none",
            }}
          />
          <button
            onClick={askAI}
            style={{
              marginLeft: "8px",
              padding: "10px 16px",
              background: "#007bff",
              color: "#fff",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
