import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const renderAIContent = (html) => {
    const container = document.createElement("div");
    container.innerHTML = html;
    return { __html: container.innerHTML };
  };

  const isFrontPage = messages.length === 0;

  return (
    <div
      style={{
        width: "900px",
        marginLeft: "20px",
        padding: "12px",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#ffffff",
        color: darkMode ? "#ffffff" : "#000000",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* HEADER */}
      <div style={{ marginBottom: isFrontPage ? "40px" : "10px" }}>
        <button onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
        </button>
      </div>

      {/* CHAT BOX */}
      <div
        style={{
          flex: isFrontPage ? "0.6" : 1,          // âœ… front page chhota
          overflowY: "auto",
          padding: "12px",
          background: darkMode ? "#1e1e1e" : "#FFF7E6",
          borderRadius: "10px",
          border: "1px solid #ccc",
        }}
      >
        {isFrontPage && (
          <h2 style={{ textAlign: "center", marginTop: "80px", opacity: 0.8 }}>
            What can I help with?
          </h2>
        )}

        {messages.map((m, i) => (
          <div
            key={i}
            style={{
              marginBottom: "16px",
              padding: "14px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              background:
                m.sender === "user"
                  ? darkMode
                    ? "#FFF7E6"
                    : "#000000"
                  : darkMode
                  ? "#2a2a2a"
                  : "#ffffff",
              color:
                m.sender === "user"
                  ? darkMode
                    ? "#000000"
                    : "#ffffff"
                  : darkMode
                  ? "#ffffff"
                  : "#000000",
            }}
          >
            <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>

            {m.sender === "ai" ? (
              <div
                style={{ marginTop: "6px" }}
                dangerouslySetInnerHTML={renderAIContent(m.text)}
              />
            ) : (
              <div style={{ marginTop: "6px" }}>{m.text}</div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* INPUT */}
      <div
        style={{
          display: "flex",
          marginTop: isFrontPage ? "20px" : "10px",
        }}
      >
        <input
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
          placeholder="Message AI..."
          style={{
            flex: 1,
            padding: "12px",
            borderRadius: "8px",
            border: "1px solid #ccc",
          }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "8px",
            padding: "12px 18px",
            background: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
