import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(false); // default light mode
  const [files, setFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState({});
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, files]);

  // ---------------- AI Chat ----------------
  const askAI = async () => {
    if (!question.trim()) return;
    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  // ---------------- File Upload ----------------
  const handleFiles = (selectedFiles) => {
    const newFiles = Array.from(selectedFiles);
    setFiles((prev) => [...prev, ...newFiles]);
    newFiles.forEach((file) => uploadFile(file));
  };

  const handleFileChange = (e) => {
    handleFiles(e.target.files);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFiles(e.dataTransfer.files);
  };

  const handleDragOver = (e) => e.preventDefault();

  const uploadFile = async (file) => {
    const formData = new FormData();
    formData.append("files", file);

    try {
      const xhr = new XMLHttpRequest();
      xhr.open("POST", "http://127.0.0.1:8000/upload");

      xhr.upload.onprogress = (event) => {
        if (event.lengthComputable) {
          setUploadProgress((prev) => ({
            ...prev,
            [file.name]: Math.round((event.loaded / event.total) * 100),
          }));
        }
      };

      xhr.onload = () => {
        if (xhr.status === 200) {
          const data = JSON.parse(xhr.responseText);
          setMessages((p) => [
            ...p,
            { text: `Uploaded: ${data.uploaded_files.join(", ")}`, sender: "ai" },
          ]);
          setUploadProgress((prev) => {
            const updated = { ...prev };
            delete updated[file.name];
            return updated;
          });
        } else {
          setMessages((p) => [
            ...p,
            { text: `Failed to upload ${file.name}`, sender: "ai" },
          ]);
        }
      };

      xhr.send(formData);
    } catch (err) {
      console.error(err);
      setMessages((p) => [
        ...p,
        { text: `Error uploading ${file.name}`, sender: "ai" },
      ]);
    }
  };

  // ---------------- AI Content Formatter ----------------
  const renderAIContent = (text) => {
    let html = text
      .replace(/^### (.*)$/gm, "<h3>üîπ $1</h3>")
      .replace(/^## (.*)$/gm, "<h2>üìå $1</h2>")
      .replace(/^# (.*)$/gm, "<h1>‚≠ê $1</h1>")
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/^\d+\.\s+(.*)$/gm, "<li>‚û°Ô∏è $1</li>")
      .replace(/^\-\s+(.*)$/gm, "<li>üëâ $1</li>")
      .replace(/\n/g, "<br/>");

    html = html.replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");
    return { __html: html };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>AI Assistant</h2>
        <button
          style={{
            padding: "10px",
            borderRadius: "6px",
            border: "none",
            cursor: "pointer",
            marginBottom: "12px",
          }}
        >
          + New Chat
        </button>
        <div style={{ opacity: 0.7 }}>History</div>
      </div>

      {/* CHAT SECTION */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
            borderRadius: "10px",
            border: "1px solid #ccc",
            position: "relative",
          }}
        >
          {/* THEME TOGGLE */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{
              position: "fixed",
              top: "16px",
              right: "16px",
              borderRadius: "20px",
              padding: "6px 14px",
              border: "none",
              cursor: "pointer",
              background: darkMode ? "#FFF7E6" : "#222",
              color: darkMode ? "#000" : "#fff",
              zIndex: 1000,
            }}
          >
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>

          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "14px",
                padding: "14px",
                borderRadius: "8px",
                border: "1px solid #ddd",
                background:
                  m.sender === "user"
                    ? darkMode
                      ? "#FFF7E6"
                      : "#000"
                    : darkMode
                    ? "#2a2a2a"
                    : "#fff",
                color:
                  m.sender === "user"
                    ? darkMode
                      ? "#000"
                      : "#fff"
                    : darkMode
                    ? "#fff"
                    : "#000",
              }}
            >
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>
              {m.sender === "ai" ? (
                <div
                  className="ai-message"
                  style={{ marginTop: "6px" }}
                  dangerouslySetInnerHTML={renderAIContent(m.text)}
                />
              ) : (
                <div style={{ marginTop: "6px" }}>{m.text}</div>
              )}
            </div>
          ))}

          <div ref={messagesEndRef} />
        </div>

        {/* INPUT BAR */}
        <div
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          style={{
            display: "flex",
            flexDirection: "column",
            padding: "10px",
            borderTop: "1px solid #ccc",
            background: darkMode ? "#121212" : "#ffffff",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            {/* File Upload Button (Left Side +) */}
            <label
              htmlFor="fileUpload"
              style={{
                cursor: "pointer",
                background: "#007bff",
                color: "#fff",
                fontWeight: "bold",
                fontSize: "20px",
                width: "36px",
                height: "36px",
                borderRadius: "50%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                marginRight: "8px",
              }}
              title="Add Files"
            >
              +
            </label>
            <input
              id="fileUpload"
              type="file"
              multiple
              style={{ display: "none" }}
              onChange={handleFileChange}
            />

            {/* Text Input */}
            <input
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && askAI()}
              placeholder="Message AI..."
              style={{
                flex: 1,
                padding: "10px 16px",
                borderRadius: "20px",
                border: "1px solid #ccc",
                marginRight: "8px",
              }}
            />

            {/* Send Button */}
            <button
              onClick={askAI}
              style={{
                padding: "10px 18px",
                background: "#007bff",
                color: "#fff",
                border: "none",
                borderRadius: "20px",
              }}
            >
              Send
            </button>
          </div>

          {/* Display Selected Files + Progress */}
          {files.length > 0 && (
            <div style={{ display: "flex", flexDirection: "column", marginTop: "6px" }}>
              {files.map((file, i) => (
                <div key={i} style={{ marginBottom: "4px" }}>
                  <span
                    style={{
                      background: "#2a2a2a",
                      color: "#fff",
                      padding: "4px 8px",
                      borderRadius: "12px",
                      fontSize: "12px",
                      marginRight: "6px",
                      display: "inline-block",
                    }}
                  >
                    {file.name}
                  </span>
                  {uploadProgress[file.name] && (
                    <div
                      style={{
                        height: "6px",
                        width: `${uploadProgress[file.name]}%`,
                        background: "#007bff",
                        borderRadius: "3px",
                        marginTop: "2px",
                        transition: "width 0.2s",
                      }}
                    />
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Chat;
