import React, { useState } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);

  const askAI = async () => {
    if (!question) return;

    // ðŸŸ¦ USER message add
    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );

      const data = await res.json();

      // ðŸŸ© AI message add
      setMessages((prev) => [...prev, { text: data.answer, sender: "ai" }]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { text: "Error: Backend not running", sender: "ai" }
      ]);
    }
  };

  return (
    <div
      style={{
        border: "2px solid #4A00E0",
        padding: "20px",
        borderRadius: "10px",
        background: "#F5F3FF"
      }}
    >
      <h2
        style={{
          fontSize: "24px",
          fontWeight: "bold",
          color: "#D10000",
          marginBottom: "10px"
        }}
      >
        Chat Window
      </h2>

      {/* MESSAGE BOX */}
      <div
        style={{
          height: "250px",
          overflowY: "auto",
          marginBottom: "15px",
          padding: "10px",
          background: "white",
          borderRadius: "8px",
          border: "1px solid #999"
        }}
      >
        {messages.map((msg, index) => (
          <p
            key={index}
            style={{
              textAlign: msg.sender === "user" ? "right" : "left",
              background: msg.sender === "user" ? "#D2F8D2" : "#FFEBEB",
              padding: "8px",
              borderRadius: "8px",
              marginBottom: "8px",
              color: msg.sender === "user" ? "#006600" : "#990000"
            }}
          >
            {msg.text}
          </p>
        ))}
      </div>

      {/* INPUT */}
      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="Ask something..."
        style={{
          width: "100%",
          padding: "10px",
          borderRadius: "8px",
          border: "1px solid #999",
          marginBottom: "10px"
        }}
      />

      {/* BUTTON */}
      <button
        onClick={askAI}
        style={{
          width: "100%",
          padding: "12px",
          fontSize: "18px",
          background: "#4A00E0",
          color: "white",
          border: "none",
          fontWeight: "bold",
          borderRadius: "8px",
          cursor: "pointer"
        }}
      >
        ASK AI
      </button>
    </div>
  );
}

export default Chat;
