import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;
    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "‚ùå Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  /* üî• AUTO FORMAT: HEADINGS + BOLD + BULLETS + NUMBERING + EMOJIS */
  const renderAIContent = (text) => {
    let html = text
      // headings with emojis
      .replace(/^### (.*)$/gm, "### üîπ $1")
      .replace(/^## (.*)$/gm, "## üìå $1")
      .replace(/^# (.*)$/gm, "# ‚≠ê $1")

      // headings html
      .replace(/^### (.*)$/gm, "<h3>$1</h3>")
      .replace(/^## (.*)$/gm, "<h2>$1</h2>")
      .replace(/^# (.*)$/gm, "<h1>$1</h1>")

      // bold
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")

      // numbered list: 1. 2. 3.
      .replace(/^\d+\.\s+(.*)$/gm, "<li>$1</li>")

      // bullet list
      .replace(/^\-\s+(.*)$/gm, "<li>üëâ $1</li>")

      // new lines
      .replace(/\n/g, "<br/>");

    // ordered list
    html = html.replace(/(<li>.*<\/li>)/gs, "<ol>$1</ol>");

    return { __html: html };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div style={{ width: "240px", background: "#1c1c1c", color: "#fff", padding: "16px" }}>
        <h2>ü§ñ AI Assistant</h2>
        <button style={{ padding: "10px", margin: "10px 0" }}>+ New Chat</button>
        <div style={{ opacity: 0.7 }}>History</div>
      </div>

      {/* CHAT */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
            borderRadius: "10px",
          }}
        >
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{ position: "fixed", top: 16, right: 16, zIndex: 1000 }}
          >
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>

          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "14px",
                padding: "14px",
                borderRadius: "8px",
                background: m.sender === "ai" ? "#2a2a2a" : "#FFF7E6",
              }}
            >
              <strong>{m.sender === "ai" ? "AI ü§ñ:" : "You üôã:"}</strong>

              {m.sender === "ai" ? (
                <div
                  className="ai-message"
                  style={{ marginTop: "6px" }}
                  dangerouslySetInnerHTML={renderAIContent(m.text)}
                />
              ) : (
                <div style={{ marginTop: "6px" }}>{m.text}</div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* INPUT */}
        <div style={{ display: "flex", padding: "10px" }}>
          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{ flex: 1, padding: "10px" }}
          />
          <button onClick={askAI} style={{ marginLeft: "8px" }}>
            Send üöÄ
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
