import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await response.json();

      let aiText = typeof data.answer === "string" ? data.answer : JSON.stringify(data.answer);

      // Replace line breaks with <br> and keep Markdown code blocks
      const formattedText = aiText
        .replace(/```/g, "<pre>") // start/end code block
        .replace(/\n/g, "<br/>")  // line breaks
        .replace(/^\* /gm, "• "); // bullets

      setMessages((prev) => [...prev, { text: formattedText, sender: "ai" }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  return (
    <div style={{ maxWidth: "700px", margin: "30px auto", fontFamily: "Arial, sans-serif" }}>
      <div
        style={{
          border: "1px solid gray",
          padding: "6px",         // reduced padding
          height: "400px",        // smaller height
          maxHeight: "400px",
          fontSize: "14px",       // smaller font
          overflowY: "auto",
          borderRadius: "6px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              textAlign: msg.sender === "ai" ? "left" : "right",
              margin: "4px 0",   // smaller spacing between messages
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>{" "}
            {msg.sender === "ai" ? (
              <div
                dangerouslySetInnerHTML={{ __html: msg.text }}
                style={{ whiteSpace: "pre-wrap", fontFamily: "monospace" }}
              />
            ) : (
              msg.text
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ marginTop: "8px", display: "flex" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{
            flex: 1,
            padding: "6px",          // smaller input
            borderRadius: "4px",
            border: "1px solid gray",
            fontSize: "14px",        // smaller font
          }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "5px",
            padding: "6px 12px",     // smaller button
            borderRadius: "4px",
            border: "none",
            backgroundColor: "#007bff",
            color: "white",
            cursor: "pointer",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
