import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((prev) => [...prev, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { text: "Backend error", sender: "ai" },
      ]);
    }
  };

  const copyCode = (pre) => {
    navigator.clipboard.writeText(pre.innerText);
  };

  const renderAI = (html) => {
    const div = document.createElement("div");
    div.innerHTML = html;

    div.querySelectorAll("pre").forEach((pre) => {
      pre.style.position = "relative";
      pre.style.paddingTop = "36px";

      const btn = document.createElement("button");
      btn.innerText = "ðŸ“‹ Copy";
      btn.style.position = "absolute";
      btn.style.top = "6px";
      btn.style.right = "6px";
      btn.style.cursor = "pointer";
      btn.style.fontSize = "12px";

      btn.onclick = () => copyCode(pre);
      pre.appendChild(btn);
    });

    return { __html: div.innerHTML };
  };

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "900px",
        margin: "20px", // âœ… LEFT ALIGN FIX
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#ffffff",
        color: darkMode ? "#ffffff" : "#000000",
        borderRadius: "10px",
        padding: "12px",
      }}
    >
      <h2>AI Assistant</h2>

      <div
        style={{
          background: darkMode ? "#1e1e1e" : "#FFF7E6",
          height: "450px",
          overflowY: "auto",
          padding: "16px",
          borderRadius: "10px",
        }}
      >
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              marginBottom: "16px",
              padding: "14px",
              borderRadius: "8px",
              textAlign: "left",
              background:
                msg.sender === "user"
                  ? darkMode
                    ? "#FFF7E6"
                    : "#000000"
                  : darkMode
                  ? "#2a2a2a"
                  : "#ffffff",
              color:
                msg.sender === "user"
                  ? darkMode
                    ? "#000000"
                    : "#ffffff"
                  : darkMode
                  ? "#ffffff"
                  : "#000000",
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>

            {msg.sender === "ai" ? (
              <div
                style={{ marginTop: "8px" }}
                dangerouslySetInnerHTML={renderAI(msg.text)}
              />
            ) : (
              <div style={{ marginTop: "8px" }}>{msg.text}</div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ display: "flex", marginTop: "12px" }}>
        <input
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
          style={{ flex: 1, padding: "10px" }}
        />
        <button
          onClick={askAI}
          style={{ marginLeft: "8px", padding: "10px 20px" }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
