import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((p) => [...p, { text: question, sender: "user" }]);
    setQuestion("");

    try {
      const res = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await res.json();
      setMessages((p) => [...p, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((p) => [
        ...p,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    }
  };

  /* ðŸ”¥ MARKDOWN â†’ HTML CONVERTER (MAIN FIX) */
  const renderAIContent = (text) => {
    let html = text
      .replace(/^### (.*)$/gm, "<h3>$1</h3>")
      .replace(/^## (.*)$/gm, "<h2>$1</h2>")
      .replace(/^# (.*)$/gm, "<h1>$1</h1>")
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/^\- (.*)$/gm, "<li>$1</li>")
      .replace(/\n/g, "<br/>");

    html = html.replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");

    return { __html: html };
  };

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        display: "flex",
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#f5f5f5",
      }}
    >
      {/* SIDEBAR */}
      <div
        style={{
          width: "240px",
          background: "#1c1c1c",
          color: "#fff",
          padding: "16px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h2>AI Assistant</h2>
        <button style={{ padding: "10px", margin: "10px 0" }}>+ New Chat</button>
        <div style={{ opacity: 0.7 }}>History</div>
      </div>

      {/* CHAT AREA */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            flex: 1,
            margin: "10px",
            padding: "14px",
            overflowY: "auto",
            background: darkMode ? "#1e1e1e" : "#FFF7E6",
            borderRadius: "10px",
            border: "1px solid #ccc",
          }}
        >
          {/* THEME BUTTON */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            style={{
              position: "fixed",
              top: "16px",
              right: "16px",
              padding: "6px 14px",
              borderRadius: "20px",
              border: "none",
              cursor: "pointer",
              zIndex: 1000,
            }}
          >
            {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
          </button>

          {messages.map((m, i) => (
            <div
              key={i}
              style={{
                marginBottom: "14px",
                padding: "14px",
                borderRadius: "8px",
                background:
                  m.sender === "user"
                    ? darkMode
                      ? "#FFF7E6"
                      : "#000"
                    : darkMode
                    ? "#2a2a2a"
                    : "#fff",
                color:
                  m.sender === "user"
                    ? darkMode
                      ? "#000"
                      : "#fff"
                    : darkMode
                    ? "#fff"
                    : "#000",
              }}
            >
              <strong>{m.sender === "ai" ? "AI:" : "You:"}</strong>

              {m.sender === "ai" ? (
                <div
                  className="ai-message"
                  style={{ marginTop: "6px" }}
                  dangerouslySetInnerHTML={renderAIContent(m.text)}
                />
              ) : (
                <div style={{ marginTop: "6px" }}>{m.text}</div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* INPUT */}
        <div style={{ display: "flex", padding: "10px" }}>
          <input
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && askAI()}
            placeholder="Message AI..."
            style={{ flex: 1, padding: "10px" }}
          />
          <button onClick={askAI} style={{ marginLeft: "8px" }}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
