import React, { useState, useRef, useEffect } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  const askAI = async () => {
    if (!question.trim()) return;

    setMessages((prev) => [...prev, { text: question, sender: "user" }]);
    setQuestion("");
    setIsTyping(true);

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await response.json();

      setMessages((prev) => [...prev, { text: data.answer, sender: "ai" }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { text: "Error connecting to AI backend", sender: "ai" },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") askAI();
  };

  const copyCode = (html) => {
    const temp = document.createElement("div");
    temp.innerHTML = html;
    navigator.clipboard.writeText(temp.innerText);
    alert("Code copied!");
  };

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "900px",
        margin: "20px 0",        // âœ… LEFT aligned
        fontFamily: "Arial",
        background: darkMode ? "#121212" : "#ffffff",
        color: darkMode ? "#ffffff" : "#000000",
        padding: "10px",
        borderRadius: "10px",
      }}
    >
      {/* HEADER */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-start", // âœ… LEFT
          alignItems: "center",
          gap: "12px",
        }}
      >
        <h2 style={{ margin: 0 }}>AI Assistant</h2>
        <button
          onClick={() => setDarkMode(!darkMode)}
          style={{ padding: "6px 12px", cursor: "pointer" }}
        >
          {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
        </button>
      </div>

      {/* CHAT BOX */}
      <div
        style={{
          background: darkMode ? "#1e1e1e" : "#FFF7E6",
          border: "1px solid #ccc",
          height: "450px",
          overflowY: "auto",
          overflowX: "hidden",
          padding: "16px",
          borderRadius: "10px",
          marginTop: "10px",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              marginBottom: "16px",
              padding: "14px",
              background:
                msg.sender === "user"
                  ? "#000000"
                  : darkMode
                  ? "#2a2a2a"
                  : "#ffffff",
              color:
                msg.sender === "user"
                  ? "#ffffff"
                  : darkMode
                  ? "#ffffff"
                  : "#000000",
              borderRadius: "8px",
              border: "1px solid #ddd",
              textAlign: "left",
            }}
          >
            <strong>{msg.sender === "ai" ? "AI:" : "You:"}</strong>

            {msg.sender === "ai" ? (
              <>
                <div
                  dangerouslySetInnerHTML={{ __html: msg.text }}
                  style={{ marginTop: "8px", overflowX: "auto" }}
                />
                {msg.text.includes("<pre>") && (
                  <button
                    onClick={() => copyCode(msg.text)}
                    style={{ marginTop: "8px", cursor: "pointer" }}
                  >
                    ðŸ“‹ Copy Code
                  </button>
                )}
              </>
            ) : (
              <div style={{ marginTop: "8px" }}>{msg.text}</div>
            )}
          </div>
        ))}

        {isTyping && (
          <div style={{ fontStyle: "italic", opacity: 0.7 }}>
            AI is typing...
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* INPUT */}
      <div style={{ marginTop: "12px", display: "flex" }}>
        <input
          type="text"
          placeholder="Ask me something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={handleKeyPress}
          style={{ flex: 1, padding: "10px" }}
        />
        <button
          onClick={askAI}
          style={{
            marginLeft: "8px",
            padding: "10px 20px",
            background: "#007bff",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          Ask
        </button>
      </div>
    </div>
  );
}

export default Chat;
