import React, { useState } from "react";

function Chat() {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);

  // ðŸ‘‰ Send text to backend
  const askAI = async () => {
    if (!question.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { text: question, sender: "user" }]);

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/ask_ai?question=${encodeURIComponent(question)}`
      );
      const data = await response.json();

      setMessages((prev) => [...prev, { text: data.answer, sender: "ai" }]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { text: "Error contacting AI server.", sender: "ai" },
      ]);
    }

    setQuestion("");
  };

  // ðŸ‘‰ Trigger Python Voice backend
  const startVoice = () => {
    fetch("http://127.0.0.1:5001/start-voice") // Your voice backend endpoint
      .then(() => console.log("Voice started"))
      .catch(() => console.log("Error starting voice"));
  };

  return (
    <div
      style={{
        width: "500px",
        margin: "0 auto",
        background: "white",
        border: "1px solid #ddd",
        borderRadius: "12px",
        display: "flex",
        flexDirection: "column",
        height: "80vh",
      }}
    >
      {/* Header */}
      <div
        style={{
          padding: "16px",
          borderBottom: "1px solid #eee",
          fontSize: "20px",
          fontWeight: "600",
        }}
      >
        AI Assistant
      </div>

      {/* Messages Area */}
      <div
        style={{
          flex: 1,
          padding: "16px",
          overflowY: "auto",
          display: "flex",
          flexDirection: "column",
          gap: "12px",
        }}
      >
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              padding: "12px 16px",
              borderRadius: "12px",
              maxWidth: "80%",
              textAlign: "left",
              alignSelf: msg.sender === "user" ? "flex-end" : "flex-start",

              // User = black bubble | AI = gray bubble
              background: msg.sender === "user" ? "#000" : "#f1f1f1",
              color: msg.sender === "user" ? "white" : "black",
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          gap: "10px",
          padding: "12px",
          borderTop: "1px solid #ddd",
          background: "white",
        }}
      >
        <input
          type="text"
          placeholder="Ask something..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && askAI()}
          style={{
            flex: 1,
            padding: "12px 16px",
            borderRadius: "12px",
            border: "1px solid #dcdcdc",
            outline: "none",
            fontSize: "16px",
          }}
        />

        {/* Mic Button */}
        <button
          onClick={startVoice}
          style={{
            width: "42px",
            height: "42px",
            borderRadius: "50%",
            border: "none",
            background: "#f0f0f0",
            cursor: "pointer",
            fontSize: "20px",
          }}
        >
          <i className="fa-solid fa-microphone"></i>
        </button>

        {/* Send Button */}
        <button
          onClick={askAI}
          style={{
            width: "42px",
            height: "42px",
            borderRadius: "12px",
            border: "none",
            background: "#000",
            color: "white",
            cursor: "pointer",
            fontSize: "18px",
          }}
        >
          <i className="fa-solid fa-paper-plane"></i>
        </button>
      </div>
    </div>
  );
}

export default Chat;
